mapper.update:
update User set
user_pri_en=#{user_pri_en} where user_id=#{user_id} and
user_active=1;

mapper.select:
select * from
ActivityAndUser where activity_and_user_user_id=#{userId} and
activity_and_user_activity_id=#{activityId};

mapper.update:
update ActivityAndUser
activity_and_user_participate=#{activity_and_user_participate}, activity_and_user_time=now(),
activity_and_user_praise=#{activity_and_user_praise},
activity_and_user_attention=#{activity_and_user_attention},
activity_and_user_signin=#{activity_and_user_signin}
where activity_and_user_user_id=#{userId} and activity_and_user_activity_id=#{activityId}

mapper.insert:
insert into ActivityAndUser( activity_and_user_user_id, activity_and_user_activity_id, activity_and_user_time,
activity_and_user_participate,
activity_and_user_praise,
activity_and_user_attention
) values( #{activity_and_user_user_id}, #{activity_and_user_activity_id}, now(),
activity_and_user_participate},
#{activity_and_user_praise},
#{activity_and_user_attention}
);
